  const addButton = () => {
    return (
      <div className="add-to-cart-button atc-card-button">
        <button onClick={handleATC}>Add to Cart</button>
      </div>
    );
  };

  // const removeButton = () => {
  //   return (
  //     <div className="add-to-cart-button atc-card-button">
  //       <button onClick={handleRFC}>Remove</button>
  //     </div>
  //   );
  // };

  return addButton();
};

export default AddToCartButton;
